import React from 'react';
import ReactDOM from 'react-dom';

import Beer from './beer';


export default class BeerList extends React.Component<{beers:Array<string>}, {value:string}> {

    constructor(props: { beers: Array<string> }) {
        super(props);
        this.state = {value:""};
    }

    addBeer() {
        if (this.state.value.length !== 0) {
            this.props.beers = {beers: this.props.beers.concat([this.state.value])};
            this.state = {value:""};

            ReactDOM.render(
                <BeerList beers={["heineken", "pas", "pas le temps de niaiser"]}/>,
                document.getElementById('root'));
        }

    }
    handleChange(event:any) {
        this.setState({value: event.target.value.toString()});
    }

    render() {
        return (
            <div>
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                <button onClick={event => this.addBeer()}>Add Beer</button>
                <h2>Liste de bi√®res:</h2>
                <ul>
                    {this.props.beers.map((item, i) =>
                        <li key={i}>
                            <Beer name={item}/>
                        </li>
                    )}
                </ul>
            </div>
        );
    }
}
