import React, { useState } from 'react';
import './App.css';
import Beer from './component/beer';
import BeerList from "./component/beer_list";
// @ts-ignore
import {BrowserRouter, Link, Route, Switch} from "react-router-dom";
import About from "./component/about";

interface State {}

function App(props: State) {
    const [beerCount, setCount] = useState(props)
    function handleSizeChanged(evt:number) {
        //setCount(evt.target.value);
        console.log("ok");
    }
  return (
      <div className="App">
          <BrowserRouter>
              <div>
                  <nav>
                      <ul>
                          <li>
                              <Link to="/">Home</Link>
                          </li>
                          <li>
                              <Link to="/about">About</Link>
                          </li>
                      </ul>
                  </nav>

                  {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
                  <Switch>
                      <Route path="/about">
                        <About author={"Maxime Beucher"}/>
                      </Route>
                      <Route path="/">
                          <BeerList onSizeChanged={handleSizeChanged} beers={["heineken", "filme mes pieds", "pas le temps de niaiser"]}/>
                      </Route>
                  </Switch>
              </div>
          </BrowserRouter>
      </div>
  );
}

export default App;
