import React, { useState } from 'react';
import './App.css';
import Beer from './component/beer';
import BeerList from "./component/beer_list";
// @ts-ignore
import {BrowserRouter, Link, Route, Switch} from "react-router-dom";
import About from "./component/about";

function App(props: any) {
    const [beers, setCount] = useState(props.beers)
    function handleSizeChanged(evt:any) {
        setCount(evt.target.value);
    }
  return (
      <div className="App">
          <BrowserRouter>
              <div>
                  <nav>
                      <ul>
                          <li>
                              <Link to="/">Home</Link>
                          </li>
                          <li>
                              <Link to="/about">About</Link>
                          </li>
                      </ul>
                  </nav>

                  {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
                  <Switch>
                      <Route path="/about">
                        <About author={"Maxime Beucher"}/>
                        <div>et j'aime {beers.length} bi√®re(s)</div>
                      </Route>
                      <Route path="/">
                          <BeerList onSizeChanged={props.handleSizeChanged} beers={beers}/>
                      </Route>
                  </Switch>
              </div>
          </BrowserRouter>
      </div>
  );
}

export default App;
