import React from 'react';
import ReactDOM from 'react-dom';

import Beer from './beer';


export default class BeerList extends React.Component<{beers:Array<string>}, {state:any}> {

    constructor(props: { beers: Array<string> }) {
        super(props);
    }

    addBeer() {
        ReactDOM.render(
            <BeerList beers={["heineken", "filme mes pieds", "pas le temps de niaiser"]}/>,
            document.getElementById('root')
        );

    }
    handleChange(event:any) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
            <div>
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                <button onClick={event => this.addBeer()}></button>
                <h2>Liste de bi√®res:</h2>
                <ul>
                    {this.props.beers.map((item, i) =>
                        <li key={i}>
                            <Beer name={item}/>
                        </li>
                    )}
                </ul>
            </div>
        );
    }
}
