import React from 'react';
import ReactDOM from 'react-dom';

import Beer from './beer';


export default class BeerList extends React.Component<{}, { beers:Array<Beer> }> {

    constructor(props:string[]) {
        // @ts-ignore
        super(props);
        let array = Array.from(props);
        array.push("corona");
        this.setState({beers:Array.from(array).map((s, i) => new Beer(s))});
    }

    render () {
        return(
            <div>
                <h2>Liste de bi√®res:</h2>
                <ul>
                    {this.props.beers.map((item,i) =>
                        <li key={i}>
                            <Beer  id={i} name={item}/>
                        </li>
                    )}
                </ul>
            </div>
        );
    }
}
